import{show,hide}from"./animation-functions.js";const observer=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting?show(e.target):"true"===getComputedStyle(e.target).getPropertyValue("--animate-on-visible-replay")&&hide(e.target)})}),elements=document.querySelectorAll(".animation");elements.forEach(e=>{var t=getComputedStyle(e);"true"===t.getPropertyValue("--animate-on-visible")?observer.observe(e):"true"===t.getPropertyValue("--animate-on-start")&&show(e)});